// frontend/src/components/AuthComponent.js
import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

const AuthComponent = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async () => {
    try {
      const response = await axios.post('/login', { username, password });
      const token = response.data.token;

      localStorage.setItem('token', token);
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;

      setError('');
    } catch (error) {
      console.error('Login failed', error);
      setError('Invalid username or password');
    }
  };

  const history = useHistory();

  const handleRegister = () => {
    history.push('/registration');
    setError('');
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    delete axios.defaults.headers.common['Authorization'];
  };

  const handleProtectedRequest = async () => {
    try {
      const response = await axios.get('/dashboard');
      console.log('Protected response:', response.data);
    } catch (error) {
      console.error('Protected request failed', error);
      setError('Authentication failed. Please log in.');
    }
  };

  return (
    <div style={{ maxWidth: '400px', margin: 'auto', padding: '20px', border: '1px solid #ccc', borderRadius: '5px' }}>
      <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>Login</h2>
      <div style={{ marginBottom: '15px' }}>
        <label style={{ display: 'block', marginBottom: '5px' }}>Username:</label>
        <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} style={{ width: '100%', padding: '8px' }} required />
      </div>
      <div style={{ marginBottom: '15px' }}>
        <label style={{ display: 'block', marginBottom: '5px' }}>Password:</label>
        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} style={{ width: '100%', padding: '8px' }} required />
      </div>
      <button onClick={handleLogin} style={{ width: '100%', padding: '10px', backgroundColor: '#4CAF50', color: '#fff', border: 'none', borderRadius: '3px' }}>Login</button>
      <p style={{ color: 'red', marginTop: '10px' }}>{error}</p>

      <h2 style={{ textAlign: 'center', marginTop: '20px' }}>Registration</h2>
      <button onClick={handleRegister} style={{ width: '100%', padding: '10px', backgroundColor: '#007BFF', color: '#fff', border: 'none', borderRadius: '3px' }}>Register</button>
      <p style={{ color: 'red', marginTop: '10px' }}>{error}</p>
    </div>
  );
};

export default AuthComponent;
