// frontend/src/components/AuthComponent.js
import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';
import './AuthComponent.css'; // Assuming you save this CSS in a file named AuthComponent.css

const AuthComponent = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async () => {
    try {
      const response = await axios.post('/login', { username, password });
      const token = response.data.token;

      localStorage.setItem('token', token);
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;

      setError('');
    } catch (error) {
      console.error('Login failed', error);
      setError('Invalid username or password');
    }
  };

  const history = useHistory();

  const handleRegister = () => {
    history.push('/registration');
    setError('');
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    delete axios.defaults.headers.common['Authorization'];
  };

  const handleProtectedRequest = async () => {
    try {
      const response = await axios.get('/dashboard');
      console.log('Protected response:', response.data);
    } catch (error) {
      console.error('Protected request failed', error);
      setError('Authentication failed. Please log in.');
    }
  };

  return (
    <div className="align">
      <h2>Login</h2>
      <div className="form login">
        <div className="form__field">
          <label for="login__username">Username:</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="form__input"
            placeholder="Username"
            required
          />
        </div>
        <div className="form__field">
          <label for="login__password">Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="form__input"
            placeholder="Password"
            required
          />
        </div>
        <div className="form__field">
          <input type="submit" value="Login" onClick={handleLogin} />
        </div>
      </div>
      <p className="text--center" style={{ color: 'red' }}>
        {error}
      </p>

      <h2>Registration</h2>
      <div className="align">
        <button className="form__button" onClick={handleRegister}>
          Register
        </button>
        <p className="text--center" style={{ color: 'red' }}>
          {error}
        </p>
      </div>
    </div>
  );
};

export default AuthComponent;
