// frontend/src/components/AuthComponent.js
import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';


const AuthComponent = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async () => {
    try {
      const response = await axios.post('/login', { username, password });
      const token = response.data.token;

      // Store the token in localStorage
      localStorage.setItem('token', token);

      // Include the token in future axios requests
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;

      // Redirect or update the UI as needed
      setError('');
    } catch (error) {
      console.error('Login failed', error);
      setError('Invalid username or password');
    }
  };

    // Initialize the history hook
    const history = useHistory();

    const handleRegister = () => {
      // Redirect to the registration page
      history.push('/registration');

      // Clear the error state
      setError('');
    };


  const handleLogout = () => {
    // Remove the token from localStorage
    localStorage.removeItem('token');

    // Remove the token from axios headers
    delete axios.defaults.headers.common['Authorization'];

    // Redirect or update the UI as needed
  };

  const handleProtectedRequest = async () => {
    try {
      // Make a request to a protected route
      const response = await axios.get('/dashboard');
      console.log('Protected response:', response.data);
    } catch (error) {
      console.error('Protected request failed', error);
      setError('Authentication failed. Please log in.');
    }
  };

  return (
<div style={{ textAlign: 'center', margin: '50px' }}>
  <h2 style={{ marginBottom: '20px' }}>Login</h2>
  <div style={{ marginBottom: '10px' }}>
    <label style={{ marginRight: '10px' }}>Username:</label>
    <input
      type="text"
      value={username}
      onChange={(e) => setUsername(e.target.value)}
      style={{ padding: '5px', borderRadius: '5px' }}
      required
    />
  </div>
  <div style={{ marginBottom: '20px' }}>
    <label style={{ marginRight: '10px' }}>Password:</label>
    <input
      type="password"
      value={password}
      onChange={(e) => setPassword(e.target.value)}
      style={{ padding: '5px', borderRadius: '5px' }}
      required
    />
  </div>
  <button
    onClick={handleLogin}
    style={{
      backgroundColor: '#4CAF50',
      color: 'white',
      padding: '10px',
      borderRadius: '5px',
      cursor: 'pointer',
    }}
  >
    Login
  </button>
  <p style={{ color: 'red', marginTop: '10px' }}>{error}</p>

  <h2 style={{ marginTop: '20px' }}>Registration</h2>
  <button
    onClick={handleRegister}
    style={{
      backgroundColor: '#008CBA',
      color: 'white',
      padding: '10px',
      borderRadius: '5px',
      cursor: 'pointer',
    }}
  >
    Register
  </button>
  <p style={{ color: 'red', marginTop: '10px' }}>{error}</p>
</div>

  );
};

export default AuthComponent;
