// frontend/src/components/AuthComponent.js
import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';


const AuthComponent = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async () => {
    try {
      const response = await axios.post('/login', { username, password });
      const token = response.data.token;

      // Store the token in localStorage
      localStorage.setItem('token', token);

      // Include the token in future axios requests
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;

      // Redirect or update the UI as needed
      setError('');
    } catch (error) {
      console.error('Login failed', error);
      setError('Invalid username or password');
    }
  };

    // Initialize the history hook
    const history = useHistory();

    const handleRegister = () => {
      // Redirect to the registration page
      history.push('/registration');

      // Clear the error state
      setError('');
    };


  const handleLogout = () => {
    // Remove the token from localStorage
    localStorage.removeItem('token');

    // Remove the token from axios headers
    delete axios.defaults.headers.common['Authorization'];

    // Redirect or update the UI as needed
  };

  const handleProtectedRequest = async () => {
    try {
      // Make a request to a protected route
      const response = await axios.get('/dashboard');
      console.log('Protected response:', response.data);
    } catch (error) {
      console.error('Protected request failed', error);
      setError('Authentication failed. Please log in.');
    }
  };

  return (
    <body className="align">
      <div className="grid">
        <form onSubmit={handleLogin} className="form login">
          <div className="form__field">
            <label htmlFor="login__username">
              <svg className="icon">
                <use xlinkHref="#icon-user"></use>
              </svg>
              <span className="hidden">Username</span>
            </label>
            <input
              autoComplete="username"
              id="login__username"
              type="text"
              name="username"
              className="form__input"
              placeholder="Username"
              required
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </div>

          <div className="form__field">
            <label htmlFor="login__password">
              <svg className="icon">
                <use xlinkHref="#icon-lock"></use>
              </svg>
              <span className="hidden">Password</span>
            </label>
            <input
              id="login__password"
              type="password"
              name="password"
              className="form__input"
              placeholder="Password"
              required
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>

          <div className="form__field">
            <input type="submit" value="Sign In" />
          </div>
        </form>

        <p className="text--center">
          Not a member? <a href="#">Sign up now</a>
          <svg className="icon">
            <use xlinkHref="#icon-arrow-right"></use>
          </svg>
        </p>
      </div>

      <svg xmlns="http://www.w3.org/2000/svg" className="icons">
        {/* Include the symbol definitions here */}
      </svg>
    </body>
  );
};

export default AuthComponent;
