// frontend/src/components/AuthComponent.js
import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';
import './AuthComponent.css'; // Assuming you save this CSS in a file named AuthComponent.css

const AuthComponent = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async () => {
    try {
      const response = await axios.post('/login', { username, password });
      const token = response.data.token;

      localStorage.setItem('token', token);
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;

      setError('');
    } catch (error) {
      console.error('Login failed', error);
      setError('Invalid username or password');
    }
  };

  const history = useHistory();

  const handleRegister = () => {
    history.push('/registration');
    setError('');
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    delete axios.defaults.headers.common['Authorization'];
  };

  const handleProtectedRequest = async () => {
    try {
      const response = await axios.get('/dashboard');
      console.log('Protected response:', response.data);
    } catch (error) {
      console.error('Protected request failed', error);
      setError('Authentication failed. Please log in.');
    }
  };

  return (
    <div className="align">
      <h2>Login</h2>
      <div className="form login">
        <div className="form__field">
        <label for="login__username"><svg class="icon">
            <use xlinkHref="#icon-user"></use>
          </svg><span class="hidden">Username</span></label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="form__input"
            placeholder="Username"
            required
          />
        </div>
        <div className="form__field">
        <label for="login__password"><svg class="icon">
            <use xlinkHref="#icon-lock"></use>
          </svg><span class="hidden">Password</span></label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="form__input"
            placeholder="Password"
            required
          />
        </div>
        <div className="form__field">
          <input type="submit" value="Login" onClick={handleLogin} />
        </div>
      </div>
      <p className="text--center" style={{ color: 'red' }}>
        {error}
      </p>

      <h2>Registration</h2>
      <div className="align">
        <p class="text--center">Not a member? <a href="" onClick={handleRegister} >Sign up now</a> <svg class="icon">  
          <use xlinkHref="#icon-arrow-right"></use>
        </svg></p>
        <p className="text--center" style={{ color: 'red' }}>
          {error}
        </p>
      </div>
      <svg xmlns="http://www.w3.org/2000/svg" class="icons">
        <symbol id="icon-arrow-right" viewBox="0 0 1792 1792">
          <path d="M1600 960q0 54-37 91l-651 651q-39 37-91 37-51 0-90-37l-75-75q-38-38-38-91t38-91l293-293H245q-52 0-84.5-37.5T128 1024V896q0-53 32.5-90.5T245 768h704L656 474q-38-36-38-90t38-90l75-75q38-38 90-38 53 0 91 38l651 651q37 35 37 90z" />
        </symbol>
        <symbol id="icon-lock" viewBox="0 0 1792 1792">
          <path d="M640 768h512V576q0-106-75-181t-181-75-181 75-75 181v192zm832 96v576q0 40-28 68t-68 28H416q-40 0-68-28t-28-68V864q0-40 28-68t68-28h32V576q0-184 132-316t316-132 316 132 132 316v192h32q40 0 68 28t28 68z" />
        </symbol>
        <symbol id="icon-user" viewBox="0 0 1792 1792">
          <path d="M1600 1405q0 120-73 189.5t-194 69.5H459q-121 0-194-69.5T192 1405q0-53 3.5-103.5t14-109T236 1084t43-97.5 62-81 85.5-53.5T538 832q9 0 42 21.5t74.5 48 108 48T896 971t133.5-21.5 108-48 74.5-48 42-21.5q61 0 111.5 20t85.5 53.5 62 81 43 97.5 26.5 108.5 14 109 3.5 103.5zm-320-893q0 159-112.5 271.5T896 896 624.5 783.5 512 512t112.5-271.5T896 128t271.5 112.5T1280 512z" />
        </symbol>
      </svg>
    </div>

  );
};

export default AuthComponent;
